def solve():
    s = input().strip()
    vowels = set('aeiou')

    def move_forward(char, steps):
        return chr((ord(char) - ord('a') + steps) % 26 + ord('a'))

    def move_backward(char, steps):
        return chr((ord(char) - ord('a') - steps) % 26 + ord('a'))

    result = ""

    for char in s:
        original_char = char

        # Handle exceptions first
        if char in 'code':
            if char == 'c' or char == 'o':
                char = move_backward(char, 1)
            elif char == 'd':
                char = move_backward(char, 3)
            elif char == 'e':
                char = move_backward(char, 4)
        else:
            # Apply normal rules
            if char in vowels:
                char = move_backward(char, 5)
            else:  # consonant
                char = move_forward(char, 9)

        # Check if transformed character is one of the exception characters
        if char in 'code':
            char = original_char

        result += char

    print(result)

solve()

def tiyFizzBuzz(s):
    vowels = set('AEIOUaeiou')
    result = []

    for char in s:
        if char.isalpha():
            if char.isupper():
                if char in 'AEIOU':
                    result.append("Iron Yard")
                else:
                    result.append("Iron")
            else:  # lowercase
                if char in 'aeiou':
                    result.append("Yard")
                else:
                    result.append(char)
        else:
            result.append(char)

    return ''.join(result)

