from collections import Counter

def get_score(dice):
    counts = Counter(dice)
    score = 0
    used = Counter()

    # Check for straight
    if sorted(dice) == [1, 2, 3, 4, 5, 6]:
        return 1000

    # Check for three pairs
    if len(counts) >= 3 and all(count == 2 for count in counts.values()):
        return 750

    # Check for six of a kind
    for num, count in counts.items():
        if count >= 6:
            if num == 1:
                score += 4 * 1000
            else:
                score += 4 * num * 100
            used[num] += 6
            break
        elif count >= 5:
            if num == 1:
                score += 3 * 1000
            else:
                score += 3 * num * 100
            used[num] += 5
            break
        elif count >= 4:
            if num == 1:
                score += 2 * 1000
            else:
                score += 2 * num * 100
            used[num] += 4
            break
        elif count >= 3:
            if num == 1:
                score += 1000
            else:
                score += num * 100
            used[num] += 3

    # Add points for remaining 1s and 5s
    for num, count in counts.items():
        remaining = count - used[num]
        if num == 1:
            score += remaining * 100
        elif num == 5:
            score += remaining * 50

    return score if score > 0 else "Zonk"

# Read input and process
dice_input = input().strip()[1:-1].split(',')
dice = [int(d.strip()) for d in dice_input]
result = get_score(dice)
print(result)

