def expanded_form(num):
    num_str = str(num)
    length = len(num_str)
    result = []

    for i, digit in enumerate(num_str):
        if digit != '0':
            # Calculate the power of 10 for the current digit
            power_of_10 = 10 ** (length - i - 1)
            # Add the term to the result list
            result.append(f"{digit}*{power_of_10}")

    # Join all terms with '+'
    return "+".join(result)

# Read input
num = int(input())

# Handle special case for 0
if num == 0:
    print("")
else:
