def alphabet_war(fight):
    # Define the powers of left and right side letters
    left_powers = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_powers = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Convert the fight string to a list for easier manipulation
    battlefield = list(fight)
    n = len(battlefield)

    # Mark positions to be destroyed by bombs
    destroyed = [False] * n

    # Process bombs and mark adjacent positions for destruction
    for i in range(n):
        if battlefield[i] == '*':
            # Destroy the bomb position itself
            destroyed[i] = True
            # Destroy left adjacent position if exists
            if i > 0:
                destroyed[i-1] = True
            # Destroy right adjacent position if exists
            if i < n-1:
                destroyed[i+1] = True

    # Calculate powers for left and right sides
    left_power = 0
    right_power = 0

    # Iterate through the battlefield
    for i in range(n):
        # Skip destroyed positions
        if destroyed[i]:
            continue

        # Add to left power if it's a left side letter
        if battlefield[i] in left_powers:
            left_power += left_powers[battlefield[i]]
        # Add to right power if it's a right side letter
        elif battlefield[i] in right_powers:
            right_power += right_powers[battlefield[i]]

    # Determine the winner
    if left_power > right_power:
        return "Left side wins!"
    elif right_power > left_power:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and call the function
fight = input().strip()
result = alphabet_war(fight)
print(result)

