import math

def getAllPrimeFactors(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 1:
        return [1]

    factors = []
    d = 2
    while d * d <= n:
        while n % d == 0:
            factors.append(d)
            n //= d
        d += 1
    if n > 1:
        factors.append(n)
    return factors

def getUniquePrimeFactorsWithCount(n):
    if not isinstance(n, int) or n <= 0:
        return [[], []]
    if n == 1:
        return [[1], [1]]

    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]

    unique_factors = []
    counts = []
    i = 0
    while i < len(factors):
        current = factors[i]
        unique_factors.append(current)
        count = 1
        while i + count < len(factors) and factors[i + count] == current:
            count += 1
        counts.append(count)
        i += count

    return [unique_factors, counts]

def getUniquePrimeFactorsWithProducts(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 1:
        return [1]

    unique_factors_with_count = getUniquePrimeFactorsWithCount(n)
    if not unique_factors_with_count[0]:
        return []

    factors, counts = unique_factors_with_count
    products = []
    for i in range(len(factors)):
        products.append(factors[i] ** counts[i])
    return products

