def almostIncreasingSequence(sequence):
    def is_strictly_increasing(seq):
        for i in range(1, len(seq)):
            if seq[i] <= seq[i - 1]:
                return False
        return True

    for i in range(len(sequence)):
        # Create a new sequence without the element at index i
        new_seq = sequence[:i] + sequence[i+1:]
        if is_strictly_increasing(new_seq):
            return True
    return False

# Read input
