def maxProductToRemove(arr):
    max_product = float('-inf')
    result = float('inf')

    for i in range(len(arr)):
        # Calculate product of remaining elements
        product = 1
        for j in range(len(arr)):
            if i != j:
                product *= arr[j]

        # Update result if we found a better product
        if product > max_product:
            max_product = product
            result = arr[i]
        elif product == max_product:
            result = min(result, arr[i])

    return result

# Read input
arr = list(map(int, input().strip()[1:-1].split(',')))

# Solve and print result
print(maxProductToRemove(arr))

def sieve_of_eratosthenes(limit):
    is_prime = [True] * (limit)
    is_prime[0] = False
    if limit > 1:
        is_prime[1] = False
    for i in range(2, int(limit**0.5)+1):
        if is_prime[i]:
            for j in range(i*i, limit, i):
                is_prime[j] = False
    return [i for i in range(limit) if is_prime[i]]

def prime_primes(N):
    primes = sieve_of_eratosthenes(N)
    count = 0
    total_sum = 0.0

    for i in range(len(primes)):
        for j in range(i+1, len(primes)):
            p_a = primes[i]
            p_b = primes[j]
            if p_a < p_b:
                count += 1
                total_sum += p_a / p_b

    integer_part = int(total_sum)
    return [count, integer_part]

N = int(input())
result = prime_primes(N)
print(result)

