import sys
from functools import lru_cache

sys.setrecursionlimit(10000)

n = int(input())
k = int(input())

@lru_cache(maxsize=None)
def count_ways(num, remaining_k):
    if remaining_k == 1:
        return 1 if num >= 1 else 0

    total = 0
    for i in range(1, int(num**0.5) + 1):
        if num % i == 0:
            j = num // i
            # Case 1: i is the first factor
            total += count_ways(j, remaining_k - 1)
            # Case 2: num//i is the first factor (avoid double counting when i == j)
            if i != j:
                total += count_ways(i, remaining_k - 1)

    return total

if k == 1:
    print(1)
else:
    print(count_ways(n, k))

