import sys
from math import gcd
from functools import reduce

def gcd_of_list(lst):
    return reduce(gcd, lst)

def main():
    line = input().strip()
    nums = list(map(int, line[1:-1].split(',')))

    # If GCD is not 1, there are infinitely many numbers that cannot be represented
    if gcd_of_list(nums) != 1:
        print(-1)
        return

    # Frobenius number for coprime integers
    # For two numbers a and b, it's g(a,b) = ab - a - b
    # But for more than two, we need to compute the largest number that cannot be represented

    # Dynamic programming approach to find all representable numbers up to a reasonable limit
    max_val = max(nums)
    # Upper bound for checking: (max_val-1)^2 is sufficient for most cases
    limit = min(10**6, (max_val + 1) ** 2)

    # dp[i] will be True if i can be represented
    dp = [False] * (limit + 1)
    dp[0] = True

    for i in range(limit + 1):
        if dp[i]:
            for num in nums:
                if i + num <= limit:
                    dp[i + num] = True

    # Find the largest number that cannot be represented
    result = -1
    for i in range(limit, -1, -1):
        if not dp[i]:
            result = i
            break

    # Check if there are infinitely many by verifying the tail
    # If all numbers after some point are representable, then result is finite
    # Otherwise, it's infinite (but we already checked GCD)

    # A safe check: if we have a long sequence of representable numbers, we're done
    consecutive_count = 0
    for i in range(limit - max_val, limit + 1):
        if dp[i]:
            consecutive_count += 1
        else:
            consecutive_count = 0

        if consecutive_count > max_val:
            break
    else:
        # Not enough consecutive representable numbers found
        if result == -1:
            result = 0

    print(result)

if __name__ == "__main__":
    main()

