def total_inc_dec(x):
    if x == 0:
        return 1

    # Memoization dictionary for dynamic programming
    memo = {}

    def count_increasing(prev_digit, remaining_digits):
        if remaining_digits == 0:
            return 1
        if (prev_digit, remaining_digits) in memo:
            return memo[(prev_digit, remaining_digits)]

        count = 0
        for digit in range(prev_digit, 10):
            count += count_increasing(digit, remaining_digits - 1)

        memo[(prev_digit, remaining_digits)] = count
        return count

    # Count all increasing numbers with up to x digits
    increasing_count = 0
    for digits in range(1, x + 1):
        for first_digit in range(1, 10):  # First digit can't be 0
            increasing_count += count_increasing(first_digit, digits - 1)

    # Count all decreasing numbers with up to x digits
    memo.clear()

    def count_decreasing(prev_digit, remaining_digits):
        if remaining_digits == 0:
            return 1
        if (prev_digit, remaining_digits) in memo:
            return memo[(prev_digit, remaining_digits)]

        count = 0
        for digit in range(prev_digit, -1, -1):
            count += count_decreasing(digit, remaining_digits - 1)

        memo[(prev_digit, remaining_digits)] = count
        return count

    decreasing_count = 0
    for digits in range(1, x + 1):
        for first_digit in range(0, 10):
            decreasing_count += count_decreasing(first_digit, digits - 1)

    # Count numbers that are both increasing and decreasing (single digits and repetitions)
    # These are numbers like 1, 2, ..., 9, 11, 22, ..., 99, 111, 222, ..., 999, etc.
    both_count = 0
    for digits in range(1, x + 1):
        both_count += 9  # For each digit length, there are 9 such numbers (11...1, 22...2, ..., 99...9)

    # Add 1 for the number 0
    return increasing_count + decreasing_count - both_count + 1

x = int(input())
