def main():
    import sys
    sys.setrecursionlimit(1 << 25)

    n, m = map(int, sys.stdin.readline().split())
    grid = [sys.stdin.readline().strip() for _ in range(n)]

    # dp[i][j] = number of ways to reach (i,j) without pushing any rocks
    dp = [[0]*m for _ in range(n)]
    dp[0][0] = 1

    # Precompute for each row and column: can we push rocks all the way?
    # For each cell, precompute how far right/down a rock can be pushed

    # rock_right[i][j] = how many consecutive rocks starting from (i,j) going right
    rock_right = [[0]*m for _ in range(n)]
    for i in range(n):
        count = 0
        for j in range(m-1, -1, -1):
            if grid[i][j] == 'R':
                count += 1
                rock_right[i][j] = count
            else:
                count = 0
                rock_right[i][j] = 0

    # rock_down[i][j] = how many consecutive rocks starting from (i,j) going down
    rock_down = [[0]*m for _ in range(n)]
    for j in range(m):
        count = 0
        for i in range(n-1, -1, -1):
            if grid[i][j] == 'R':
                count += 1
                rock_down[i][j] = count
            else:
                count = 0
                rock_down[i][j] = 0

    # can_move_right[i][j] = can we move right from (i,j)?
    can_move_right = [[True]*m for _ in range(n)]
    for i in range(n):
        for j in range(m):
            if j == m - 1:
