import sys
from math import gcd
from functools import reduce

def gcd_of_list(lst):
    return reduce(gcd, lst)

def main():
    line = input().strip()
    nums = list(map(int, line[1:-1].split(',')))

    # If GCD is not 1, there are infinitely many numbers that cannot be formed
    if gcd_of_list(nums) != 1:
        print(-1)
        return

    # If GCD is 1, there exists a largest number that cannot be expressed
    # Using Frobenius number formula for two numbers: ab - a - b
    # But for more than two, we need to compute it differently

    # Sort the numbers
    nums.sort()

    # If 1 is present, every number can be formed
    if nums[0] == 1:
        print(0)
        return

    # Dynamic programming approach to find the largest number that cannot be formed
    # We use the fact that once we can form all numbers from some point,
    # we can form all larger numbers too

    max_check = max(nums) * max(nums)  # Upper bound for checking
    dp = [False] * (max_check + max(nums) + 1)
    dp[0] = True

    for i in range(len(dp)):
        if dp[i]:
            for num in nums:
                if i + num < len(dp):
                    dp[i + num] = True

    # Find the largest number that cannot be formed
    result = -1
    for i in range(len(dp) - 1, -1, -1):
        if not dp[i]:
            result = i
            break

    # Verify that all numbers after some point can be formed
    # Find the point after which all numbers can be formed
    start_all_formable = len(dp)
    for i in range(len(dp) - 1, -1, -1):
        if not dp[i]:
            start_all_formable = i + 1
            break

    # Check if all numbers from start_all_formable to end of dp array are formable
    all_formable = True
    for i in range(start_all_formable, len(dp)):
        if not dp[i]:
            all_formable = False
            break

    if not all_formable:
        print(-1)
    else:
        print(result)

if __name__ == "__main__":
    main()

