def differentiate(equation, point):
    # Split the equation into terms by '+' and '-'
    terms = []
    current_term = ""
    for char in equation:
        if char in '+-' and current_term:
            terms.append(current_term)
            current_term = char
        else:
            current_term += char
    terms.append(current_term)

    # Parse each term and compute derivative
    derivative = 0
    for term in terms:
        term = term.strip()
        if not term:
            continue

        # Handle sign
        sign = 1
        if term.startswith('-'):
            sign = -1
            term = term[1:]
        elif term.startswith('+'):
            term = term[1:]

        # Parse coefficient and exponent
        if 'x' not in term:
            # Constant term, derivative is 0
            continue
        elif term == 'x':
            # Term is just 'x', coefficient is 1, exponent is 1
            coeff = 1
            exp = 1
        elif term == '-x':
            # Term is '-x', coefficient is -1, exponent is 1
            coeff = -1
            exp = 1
        elif term.startswith('x'):
            # Term starts with 'x', coefficient is 1
            coeff = 1
            if '^' in term:
                exp = int(term.split('^')[1])
            else:
                exp = 1
        else:
            # Term has explicit coefficient
            if '*x' in term:
                coeff_str, rest = term.split('*x', 1)
            elif 'x' in term:
                coeff_str = term.split('x')[0]
                rest = term[len(coeff_str):]
            else:
                coeff_str = term
                rest = ''

            try:
                coeff = int(coeff_str)
            except ValueError:
                coeff = 1 if coeff_str == '' else int(coeff_str)

            if '^' in rest:
                exp = int(rest.split('^')[1])
            elif 'x' in rest or rest == '':
                exp = 1
            else:
                exp = 0

        # Apply differentiation rule: d/dx(ax^n) = n*a*x^(n-1)
        # At point x, this becomes n*a*(x^(n-1))
        if exp == 0:
            # Derivative of constant is 0
            derivative_value = 0
        else:
            derivative_value = sign * exp * coeff * (point ** (exp - 1))

        derivative += derivative_value

    return derivative

# Read inputs
equation = input().strip()
point = int(input().strip())

# Compute and print result
result = differentiate(equation, point)
print(result)

