def main():
    n = int(input())
    a = list(map(int, input().split()))

    # Count how many times each value appears as a parent
    count = [0] * (n + 1)
    for x in a:
        if x <= n:
            count[x] += 1

    # Find all vertices that can be roots (appear as their own parent)
    roots = []
    for i in range(1, n + 1):
        if count[i] > 0 and a[i-1] == i:
            roots.append(i)

    # If there's exactly one root, check if the structure is valid
    if len(roots) == 1:
        root = roots[0]
        # Build adjacency list to check connectivity
        children = [[] for _ in range(n + 1)]
        for i in range(n):
            if a[i] != i + 1:
                children[a[i]].append(i + 1)

        # BFS to check if all nodes are reachable from root
        visited = [False] * (n + 1)
        queue = [root]
        visited[root] = True
        nodes_visited = 1

        while queue:
            node = queue.pop(0)
            for child in children[node]:
                if not visited[child]:
                    visited[child] = True
                    queue.append(child)
                    nodes_visited += 1

        # If all nodes visited, it's valid
        if nodes_visited == n:
            print(0)
            print(*a)
            return

    # Need to fix the sequence
    changes = 0
    result = a[:]

    # Find positions where a[i] > n (invalid)
    invalid_positions = []
    for i in range(n):
        if a[i] > n:
            invalid_positions.append(i)

    # Count parents
    parent_count = [0] * (n + 1)
    for x in a:
        if 1 <= x <= n:
            parent_count[x] += 1

    # Find available nodes to be roots (nodes that don't appear as children)
    is_child = [False] * (n + 1)
    for i in range(n):
        if 1 <= a[i] <= n and a[i] != i + 1:
            is_child[a[i]] = True

    # Find a node that can be made root
    root_candidate = -1
    for i in range(1, n + 1):
        if not is_child[i]:
            root_candidate = i
            break

    # If no root candidate found, use node 1 as root
    if root_candidate == -1:
        root_candidate = 1

    # Fix the root
    if result[root_candidate - 1] != root_candidate:
        result[root_candidate - 1] = root_candidate
        changes += 1

    # Make sure all other nodes point to valid parents
    for i in range(n):
        if i + 1 != root_candidate and (result[i] < 1 or result[i] > n or result[i] == i + 1):
            # Point to root candidate
            if result[i] != root_candidate:
                result[i] = root_candidate
                changes += 1

    print(changes)
    print(*result)

if __name__ == "__main__":
    main()

