def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def main():
    n = int(input())

    # Sieve of Eratosthenes to find primes up to n
    is_prime = [True] * (n + 1)
    is_prime[0] = is_prime[1] = False
    for i in range(2, int(n**0.5) + 1):
        if is_prime[i]:
            for j in range(i*i, n + 1, i):
                is_prime[j] = False

    # Find all prime numbers up to n
    primes = [i for i in range(2, n + 1) if is_prime[i]]

    # Pair even numbers with themselves (they all have gcd >= 2)
    pairs = []

    # First, pair each even number with another even number
    evens = [i for i in range(2, n + 1, 2)]
    for i in range(0, len(evens) - 1, 2):
        pairs.append((evens[i], evens[i + 1]))

    # For odd numbers, try to pair them with multiples
    odds = [i for i in range(1, n + 1) if i % 2 == 1]
    used = set()
    for pair in pairs:
        used.add(pair[0])
        used.add(pair[1])

    # Try to pair remaining odd numbers with their multiples
    for i in range(len(odds)):
        if odds[i] in used:
            continue
        for j in range(i + 1, len(odds)):
            if odds[j] in used:
                continue
            if gcd(odds[i], odds[j]) > 1:
                pairs.append((odds[i], odds[j]))
                used.add(odds[i])
                used.add(odds[j])
                break

    print(len(pairs))
    for pair in pairs:
        print(pair[0], pair[1])

