from collections import deque

def main():
    n, k = map(int, input().split())
    grid = [list(map(int, input().split())) for _ in range(4)]

    # Find initial positions of cars (in rows 2 and 3)
    pos = {}
    for i in range(4):
        for j in range(n):
            if grid[i][j] != 0:
                pos[grid[i][j]] = (i, j)

    # Find target positions (in rows 1 and 4)
    target = {}
    for i in [0, 3]:  # Rows 1 and 4 (0-indexed)
        for j in range(n):
            if grid[i][j] != 0:
                target[grid[i][j]] = (i, j)

    # BFS to find solution
    # State: (grid_state, car_positions, moves_list)
    initial_state = (tuple(tuple(row) for row in grid), tuple(sorted(pos.items())), [])
    queue = deque([initial_state])
    visited = set()
    visited.add((tuple(tuple(row) for row in grid), tuple(sorted(pos.items()))))

    while queue:
        current_grid, current_pos, moves = queue.popleft()

        # Check if all cars are in place
        success = True
        for car, (r, c) in current_pos:
            if (r, c) != target.get(car, (r, c)):
                success = False
                break

        if success:
            if len(moves) <= 20000:
                print(len(moves))
                for move in moves:
                    print(move)
                return
            else:
                print(-1)
                return

        # Limit search depth to avoid excessive computation
        if len(moves) >= 20000:
            continue

        # Convert current_pos to dict for easier handling
        pos_dict = dict(current_pos)

        # Find empty spaces in rows 2 and 3
        empty_spaces = []
        for i in [1, 2]:  # Rows 2 and 3 (0-indexed)
            for j in range(n):
                if current_grid[i][j] == 0:
                    empty_spaces.append((i, j))

        # Try moving each car that can move
        for car in pos_dict:
            r, c = pos_dict[car]
            # Cars can only be in rows 2 and 3 for movement
            if r not in [1, 2]:
                continue

            # Check neighbors
            for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nr, nc = r + dr, c + dc

                # Check bounds
                if not (0 <= nr < 4 and 0 <= nc < n):
                    continue

                # Check if space is empty
                if current_grid[nr][nc] != 0:
                    continue

                # Check parking rules
                if nr in [0, 3]:  # First or fourth row
                    if target.get(car, (-1, -1)) != (nr, nc):
                        continue  # Can only move to designated spot

                # Make the move
                new_grid = [list(row) for row in current_grid]
                new_grid[r][c] = 0
                new_grid[nr][nc] = car

                new_pos = dict(pos_dict)
                new_pos[car] = (nr, nc)

                state_key = (tuple(tuple(row) for row in new_grid), tuple(sorted(new_pos.items())))

                if state_key not in visited:
                    visited.add(state_key)
                    new_moves = moves + [f"{car} {nr+1} {nc+1}"]
                    queue.append((tuple(tuple(row) for row in new_grid), tuple(sorted(new_pos.items())), new_moves))

    print(-1)

if __name__ == "__main__":
    main()

