def main():
    import sys
    sys.setrecursionlimit(1 << 25)

    n, m = map(int, sys.stdin.readline().split())
    grid = [sys.stdin.readline().strip() for _ in range(n)]

    # Precompute pushability
    # can_push_down[i][j] = can a rock at (i,j) be pushed down?
    can_push_down = [[False]*m for _ in range(n)]
    # can_push_right[i][j] = can a rock at (i,j) be pushed right?
    can_push_right = [[False]*m for _ in range(n)]

    # Fill can_push_down
    for j in range(m):
        # Bottom row
        if n > 1 and grid[n-1][j] == 'R':
            can_push_down[n-1][j] = False
        else:
            can_push_down[n-1][j] = True

    for i in range(n-2, -1, -1):
        for j in range(m):
            if grid[i][j] == '.':
                can_push_down[i][j] = True
            else:
                # It's 'R', check the cell below
                if i+1 < n and can_push_down[i+1][j]:
                    can_push_down[i][j] = True
                else:
                    can_push_down[i][j] = False

    # Fill can_push_right
    for i in range(n):
        # Rightmost column
        if m > 1 and grid[i][m-1] == 'R':
            can_push_right[i][m-1] = False
        else:
            can_push_right[i][m-1] = True

    for j in range(m-2, -1, -1):
        for i in range(n):
            if grid[i][j] == '.':
                can_push_right[i][j] = True
            else:
                # It's 'R', check the cell to the right
                if j+1 < m and can_push_right[i][j+1]:
                    can_push_right[i][j] = True
                else:
                    can_push_right[i][j] = False

    # Now do DP
    # dp[i][j] = number of ways to reach (i,j)
    dp = [[0]*m for _ in range(n)]
    if grid[0][0] == '.':
        dp[0][0] = 1
    else:
        # There's a rock at start. Can we push it right?
        if m > 1 and can_push_right[0][0]:
            dp[0][0] = 1
        else:
            # Or can we push it down?
            if n > 1 and can_push_down[0][0]:
                dp[0][0] = 1
            else:
                # Cannot push the initial rock, so no path
                print(0)
                return

    for i in range(n):
        for j in range(m):
            if dp[i][j] == 0:
                continue
            # Move right
            if j+1 < m:
                if grid[i][j+1] == '.':
                    dp[i][j+1] = (dp[i][j+1] + dp[i][j]) % MOD
                else:
                    # It's 'R'
                    if can_push_right[i][j+1]:
                        dp[i][j+1] = (dp[i][j+1] + dp[i][j]) % MOD

            # Move down
            if i+1 < n:
                if grid[i+1][j] == '.':
                    dp[i+1][j] = (dp[i+1][j] + dp[i][j]) % MOD
                else:
                    # It's 'R'
                    if can_push_down[i+1][j]:
                        dp[i+1][j] = (dp[i+1][j] + dp[i][j]) % MOD

    print(dp[n-1][m-1])

if __name__ == "__main__":
