def transform_sentence(sentence):
    words = sentence.split(' ')
    result_words = []

    for word in words:
        # Separate punctuation from the actual word
        punctuation = ''
        clean_word = word

        # Extract trailing punctuation
        while clean_word and not clean_word[-1].isalnum():
            punctuation = clean_word[-1] + punctuation
            clean_word = clean_word[:-1]

        # Extract leading punctuation
        leading_punct = ''
        while clean_word and not clean_word[0].isalnum():
            leading_punct += clean_word[0]
            clean_word = clean_word[1:]

        # Apply transformations based on rules
        if len(clean_word) == 1:
            transformed = '0'
        elif len(clean_word) == 2:
            transformed = clean_word.upper()
        elif ',' in word and word.index(',') > 0:  # Before a comma
            idx = word.index(',')
            if idx > 0 and word[:idx].isalnum():
                transformed = clean_word.upper()
            else:
                # Count T/t in the clean word part
                t_count = clean_word.lower().count('t')
                if len(clean_word) > 6 or t_count >= 2:
                    transformed = clean_word[::-1]
                else:
                    transformed = clean_word
        else:
            # Count T/t in the clean word part
            t_count = clean_word.lower().count('t')
            if len(clean_word) > 6 or t_count >= 2:
                transformed = clean_word[::-1]
            else:
                transformed = clean_word

        # Reattach punctuations
        result_words.append(leading_punct + transformed + punctuation)

    return ' '.join(result_words)

# Read input and process
sentence = input()
print(transform_sentence(sentence))

