def mix(s1, s2):
    # Count frequency of lowercase letters in both strings
    freq1 = {}
    freq2 = {}

    for c in s1:
        if 'a' <= c <= 'z':
            freq1[c] = freq1.get(c, 0) + 1

    for c in s2:
        if 'a' <= c <= 'z':
            freq2[c] = freq2.get(c, 0) + 1

    # Build result list
    result = []

    # Get all lowercase letters
    all_letters = set(freq1.keys()) | set(freq2.keys())

    for letter in all_letters:
        count1 = freq1.get(letter, 0)
        count2 = freq2.get(letter, 0)

        # Only consider if maximum is greater than 1
        max_count = max(count1, count2)
        if max_count <= 1:
            continue

        if count1 > count2:
            result.append((max_count, f"1:{letter * max_count}"))
        elif count2 > count1:
            result.append((max_count, f"2:{letter * max_count}"))
        else:  # count1 == count2
            result.append((max_count, f"=:{letter * max_count}"))

    # Sort by length (descending) and then lexicographically (ascending)
    result.sort(key=lambda x: (-x[0], x[1]))

    # Extract the string parts and join with '/'
    return '/'.join(item[1] for item in result)

# Read input
s1 = input()
s2 = input()

# Process and print result
print(mix(s1, s2))

