def solve_runes(expression):
    # Try all possible digits for the unknown rune (0-9)
    for digit in range(10):
        # Skip if this digit already appears in the expression
        if str(digit) in expression.replace('?', ''):
            continue

        # Replace all '?' with the current digit
        test_expr = expression.replace('?', str(digit))

        # Split the expression into parts
        if '+' in test_expr and test_expr.count('=') == 1:
            left, right = test_expr.split('=')
            if '+' in left:
                a, b = left.split('+')
                try:
                    if (a.startswith('0') and len(a) > 1 and a != '0') or \
                       (b.startswith('0') and len(b) > 1 and b != '0') or \
                       (right.startswith('0') and len(right) > 1 and right != '0'):
                        continue
                    if int(a) + int(b) == int(right):
                        return digit
                except:
                    continue

        elif '-' in test_expr and test_expr.count('=') == 1:
            left, right = test_expr.split('=')
            if left.count('-') == 1 and not left.startswith('-'):
                minus_index = left.find('-')
                a = left[:minus_index]
                b = left[minus_index+1:]
                try:
                    if (a.startswith('0') and len(a) > 1 and a != '0') or \
                       (b.startswith('0') and len(b) > 1 and b != '0') or \
                       (right.startswith('0') and len(right) > 1 and right != '0'):
                        continue
                    if int(a) - int(b) == int(right):
                        return digit
                except:
                    continue
            elif left.startswith('-') and left[1:].count('-') == 1:
                # First number is negative
                first_minus = left.find('-', 1)
                a = left[:first_minus]
                b = left[first_minus+1:]
                try:
                    if (len(a) > 1 and a.startswith('-') and a[1] == '0' and len(a) > 2) or \
                       (b.startswith('0') and len(b) > 1 and b != '0') or \
                       (right.startswith('0') and len(right) > 1 and right != '0'):
                        continue
                    if int(a) - int(b) == int(right):
                        return digit
                except:
                    continue

        elif '*' in test_expr and test_expr.count('=') == 1:
            left, right = test_expr.split('=')
            if '*' in left:
                a, b = left.split('*')
                try:
                    if (a.startswith('0') and len(a) > 1 and a != '0') or \
                       (b.startswith('0') and len(b) > 1 and b != '0') or \
                       (right.startswith('0') and len(right) > 1 and right != '0'):
                        continue
                    if int(a) * int(b) == int(right):
                        return digit
                except:
                    continue

    # No valid digit found
    return -1

