def mix(s1, s2):
    # Count frequency of lowercase letters in both strings
    freq1 = {}
    freq2 = {}

    for ch in s1:
        if 'a' <= ch <= 'z':
            freq1[ch] = freq1.get(ch, 0) + 1

    for ch in s2:
        if 'a' <= ch <= 'z':
            freq2[ch] = freq2.get(ch, 0) + 1

    # Collect all characters with max frequency > 1
    result = []

    # Get all unique lowercase letters from both dictionaries
    all_chars = set(freq1.keys()) | set(freq2.keys())

    for ch in all_chars:
        count1 = freq1.get(ch, 0)
        count2 = freq2.get(ch, 0)

        max_count = max(count1, count2)

        # Only consider if max frequency is greater than 1
        if max_count > 1:
            if count1 > count2:
                result.append((max_count, 1, ch))
            elif count2 > count1:
                result.append((max_count, 2, ch))
            else:  # count1 == count2
                result.append((max_count, 3, ch))  # 3 represents '='

    # Sort by length (descending), then by prefix (ascending), then by character (ascending)
    result.sort(key=lambda x: (-x[0], x[1], x[2]))

    # Build the final string
    parts = []
    for count, prefix, ch in result:
        if prefix == 3:  # '=' case
            parts.append(f"={':' + ch * count}")
        else:
            parts.append(f"{prefix}:{ch * count}")

    return '/'.join(parts)

