def expand_number(n):
    if n == 0:
        return ""

    s = str(n)
    terms = []

    for i, digit in enumerate(s):
        if digit == '0':
            continue

        # Calculate place value
        place_value = 10 ** (len(s) - i - 1)

        # Format term
        if place_value == 1:
            terms.append(digit)
        else:
            terms.append(f"{digit}*{place_value}")

    return "+".join(terms)

