            # Destroy adjacent positions
            if i > 0:
                destroyed[i-1] = True
            if i < length - 1:
                destroyed[i+1] = True

    # Calculate powers of surviving letters
    left_total = 0
    right_total = 0

    for i in range(length):
        if not destroyed[i]:  # If not destroyed
            char = chars[i]
            if char in left_power:
                left_total += left_power[char]
            elif char in right_power:
