import sys

def getAllPrimeFactors(n):
    # Error handling
    if not isinstance(n, int) or n <= 0:
        return []

    # Edge case
    if n == 1:
        return [1]

    factors = []
    divisor = 2

    while divisor * divisor <= n:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1

    if n > 1:
        factors.append(n)

    return factors

def getUniquePrimeFactorsWithCount(n):
    # Error handling
    if not isinstance(n, int) or n <= 0:
        return [[], []]

    # Edge case
    if n == 1:
        return [[1], [1]]

    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]

    unique_factors = []
    counts = []

    for factor in factors:
        if not unique_factors or factor != unique_factors[-1]:
            unique_factors.append(factor)
            counts.append(1)
        else:
            counts[-1] += 1

    return [unique_factors, counts]

def getUniquePrimeFactorsWithProducts(n):
    # Error handling
    if not isinstance(n, int) or n <= 0:
        return []

    # Edge case
    if n == 1:
        return [1]

    unique_factors_with_count = getUniquePrimeFactorsWithCount(n)
    if not unique_factors_with_count[0]:
        return []

    factors, counts = unique_factors_with_count[0], unique_factors_with_count[1]
    products = []

    for i in range(len(factors)):
        products.append(factors[i] ** counts[i])

    return products

