def almostIncreasingSequence(sequence):
    def is_strictly_increasing(arr):
        for i in range(1, len(arr)):
            if arr[i] <= arr[i-1]:
                return False
        return True

    # Count violations and find their positions
    violations = []
    for i in range(1, len(sequence)):
        if sequence[i] <= sequence[i-1]:
            violations.append(i)

    # If no violations, already strictly increasing
    if len(violations) == 0:
        return True

    # If more than one violation, impossible with one removal
    if len(violations) > 1:
        return False

    # Exactly one violation, try removing either element involved
    violation_index = violations[0]

    # Try removing the element at violation_index - 1
    seq1 = sequence[:violation_index-1] + sequence[violation_index:]
    if is_strictly_increasing(seq1):
        return True

    # Try removing the element at violation_index
    seq2 = sequence[:violation_index] + sequence[violation_index+1:]
    if is_strictly_increasing(seq2):
        return True

    return False

