def is_prime(num):
    """Check if a number is prime"""
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False

    # Check odd divisors up to sqrt(num)
    i = 3
    while i * i <= num:
        if num % i == 0:
            return False
        i += 2
    return True

def is_mersenne_prime(n):
    """Check if n produces a Mersenne prime (2^n - 1 is prime)"""
    if n <= 0:
        return False

    # Calculate 2^n - 1
    mersenne_num = (2 ** n) - 1

    # Check if it's prime
    return is_prime(mersenne_num)

