from functools import reduce

def gcd_of_list(lst):
    return reduce(math.gcd, lst)

def solve(numbers):
    # Special case: if any number is 1, all can be represented
    if 1 in numbers:
        return 0

    # Special case: if GCD > 1, infinite numbers cannot be represented
    if gcd_of_list(numbers) > 1:
        return -1

    # Use dynamic programming to find all possible sums
    # We need to go far enough to ensure we've found the pattern
    max_num = max(numbers)

    # Upper bound: for theoretical guarantee, we might need to go quite far
    # But practically, for the given constraints, we can use a reasonable limit
    limit = min(1000000, max(10000, max_num * max_num))

    # dp[i] represents whether number i can be formed
    dp = [False] * (limit + 1)
    dp[0] = True

    for i in range(limit + 1):
        if dp[i]:
            for num in numbers:
                if i + num <= limit:
                    dp[i + num] = True

    # Find the largest number that cannot be represented
    # Once we see enough consecutive true values (equal to min(numbers)), 
    # we know all subsequent numbers can be represented

    min_num = min(numbers)
    result = -1

    # Check backwards to find the largest non-representable
    for i in range(limit, -1, -1):
        if not dp[i]:
            result = i
            break

    # Double-check by ensuring there's a sufficient consecutive sequence
    # If we have min_num consecutive representable numbers,
    # then all numbers after that can be represented
    consecutive_found = False
    for i in range(max(0, limit - 2 * min_num), limit - min_num + 1):
        all_true = True
        for j in range(i, i + min_num):
            if j <= limit and not dp[j]:
                all_true = False
                break
        if all_true:
            consecutive_found = True
            break

    if not consecutive_found and result == -1:
        # Fallback: just search forward
        for i in range(limit + 1):
            if not dp[i]:
                result = i

    return result

