def sum_of_squares(n):
    # Check if n is a perfect square
    if is_perfect_square(n):
        return 1

    # Check if n can be expressed as sum of 2 squares
    for i in range(1, int(math.sqrt(n)) + 1):
        if is_perfect_square(n - i * i):
            return 2

    # Check if n is of the form 4^a * (8b + 7)
    # This means n cannot be expressed as sum of 3 squares
    temp = n
    while temp % 4 == 0:
        temp //= 4

