def proper_fractions(d):
    if d <= 1:
        return 0

    # Calculate Euler's totient function φ(d)
    result = d

    # Check for factor 2
    if d % 2 == 0:
        result = result // 2  # result = result * (1 - 1/2)
        while d % 2 == 0:
            d = d // 2

    # Check for odd factors from 3 onwards
    i = 3
    while i * i <= d:
        if d % i == 0:
            result = result - result // i  # result = result * (1 - 1/i)
            while d % i == 0:
                d = d // i
        i += 2

    # If d is still > 1, then it's a prime factor
    if d > 1:
        result = result - result // d  # result = result * (1 - 1/d)

    return result

