def solve(s):
    vowels = "aeiou"

    # Separate vowels and consonants
    vowel_chars = []
    consonant_chars = []

    for char in s:
        if char in vowels:
            vowel_chars.append(char)
        else:
            consonant_chars.append(char)

    # Sort for lexicographically smallest arrangement
    vowel_chars.sort()
    consonant_chars.sort()

    v_count = len(vowel_chars)
    c_count = len(consonant_chars)

    # Check if alternating is possible
    if abs(v_count - c_count) > 1:
        return "failed"

    # Determine starting character
    if v_count == c_count:
        # Equal counts, start with vowel
        start_with_vowel = True
    elif v_count > c_count:
        # More vowels, start with vowel
        start_with_vowel = True
    else:
        # More consonants, start with consonant
        start_with_vowel = False

    # Build the result
    result = []
    v_index = 0
    c_index = 0

    for i in range(len(s)):
        if (i % 2 == 0 and start_with_vowel) or (i % 2 == 1 and not start_with_vowel):
            # Place vowel
            if v_index < len(vowel_chars):
                result.append(vowel_chars[v_index])
                v_index += 1
            else:
                return "failed"
        else:
            # Place consonant
            if c_index < len(consonant_chars):
                result.append(consonant_chars[c_index])
                c_index += 1
            else:
                return "failed"

    return ''.join(result)

# Read input and solve
s = input().strip()
print(solve(s))

def is_prime(num):
    """Check if a number is prime"""
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False

    # Check odd divisors up to sqrt(num)
    i = 3
    while i * i <= num:
        if num % i == 0:
            return False
        i += 2
    return True

def is_mersenne_prime(n):
    """Check if n produces a Mersenne prime (2^n - 1 is prime)"""
    if n <= 0:
        return False

    # Calculate 2^n - 1
    mersenne_num = (2 ** n) - 1

    # Check if it's prime
    return is_prime(mersenne_num)

