CHARS = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"

def char_to_digit(c):
    return CHARS.index(c)

def digit_to_char(d):
    return CHARS[d]

def base_to_decimal(s, base):
    """Convert a string in given base to decimal"""
    result = 0
    for c in s:
        result = result * base + char_to_digit(c)
    return result

def decimal_to_base(n, base):
    """Convert decimal number to string in given base"""
    if n == 0:
        return "0"
    result = ""
    while n > 0:
        result = digit_to_char(n % base) + result
        n //= base
    return result

def is_polydivisible(n_str, base):
    """Check if a number string is polydivisible in given base"""
    for i in range(1, len(n_str) + 1):
        prefix = n_str[:i]
        decimal_value = base_to_decimal(prefix, base)
        if decimal_value % i != 0:
            return False
    return True

def get_polydivisible(n, base):
    """Get the nth polydivisible number in given base"""
    if n == 1:
        return "0"

    # Generate polydivisible numbers in order
    polydivisibles = ["0"]
    candidates = ["0"]

    while len(polydivisibles) < n:
        next_candidates = []
        for current in candidates:
            # Try extending current number with each possible digit
            for digit in range(base):
                new_number = current + digit_to_char(digit)
                # Check if this new number is still polydivisible
                if is_polydivisible(new_number, base):
                    polydivisibles.append(new_number)
                    next_candidates.append(new_number)
                    if len(polydivisibles) == n:
                        return new_number
        candidates = next_candidates

    return polydivisibles[n-1]

