def words_to_number(s):
    if not s:
        return 0

    # Define word to number mappings
    word_to_num = {
        'zero': 0, 'one': 1, 'two': 2, 'three': 3, 'four': 4, 'five': 5,
        'six': 6, 'seven': 7, 'eight': 8, 'nine': 9, 'ten': 10,
        'eleven': 11, 'twelve': 12, 'thirteen': 13, 'fourteen': 14, 'fifteen': 15,
        'sixteen': 16, 'seventeen': 17, 'eighteen': 18, 'nineteen': 19, 'twenty': 20,
        'thirty': 30, 'forty': 40, 'fifty': 50, 'sixty': 60, 'seventy': 70,
        'eighty': 80, 'ninety': 90
    }

    # Handle special case for zero
    if s == 'zero':
        return 0

    # Split the string into words and remove hyphens
    words = []
    for word in s.split():
        if '-' in word:
            parts = word.split('-')
            words.extend(parts)
        else:
            words.append(word)

    # Remove "and" words
    words = [word for word in words if word != 'and']

    result = 0
    current = 0

    for word in words:
        if word == 'hundred':
            current *= 100
        elif word == 'thousand':
            result += current * 1000
            current = 0
        elif word == 'million':
            result += current * 1000000
            current = 0
        else:
            current += word_to_num[word]

    result += current
    return result

# Read input and solve
s = input().strip()
print(words_to_number(s))
