def get_score(dice):
    from collections import Counter

    counts = Counter(dice)

    # Check for straight (1,2,3,4,5,6)
    if len(counts) == 6 and all(count == 1 for count in counts.values()):
        return 1000

    # Check for three pairs
    if len(counts) == 3 and all(count == 2 for count in counts.values()):
        return 750

    total_score = 0

    # Handle six of a kind
    for die, count in counts.items():
        if count >= 6:
            if die == 1:
                total_score += 4000
            else:
                total_score += die * 100 * 4
            counts[die] -= 6

    # Handle five of a kind
    for die, count in counts.items():
        if count >= 5:
            if die == 1:
                total_score += 3000
            else:
                total_score += die * 100 * 3
            counts[die] -= 5

    # Handle four of a kind
    for die, count in counts.items():
        if count >= 4:
            if die == 1:
                total_score += 2000
            else:
                total_score += die * 100 * 2
            counts[die] -= 4

    # Handle three of a kind
    for die, count in counts.items():
        if count >= 3:
            if die == 1:
                total_score += 1000
            else:
                total_score += die * 100
            counts[die] -= 3

    # Handle remaining 1s and 5s
    total_score += counts.get(1, 0) * 100
    total_score += counts.get(5, 0) * 50

    return total_score if total_score > 0 else "Zonk"

# Read input and call function
if __name__ == "__main__":
    dice_input = eval(input())
    result = get_score(dice_input)
    print(result)

