import re

def drop_cap(s):
    def process_word(match):
        word = match.group(0)
        if len(word) > 2:
            return word[0].upper() + word[1:].lower()
        else:
            return word

    # Use regex to find all sequences of non-space characters (words)
    # and apply processing to each word
    result = re.sub(r'\S+', process_word, s)
    return result  # Return string directly, not as a list

# Read input and process
input_string = input()
output = drop_cap(input_string)
print(output)  # Print the string directly

