def expanded_form(num):
    if num == 0:
        return ""

    num_str = str(num)
    length = len(num_str)
    result_parts = []

    for i, digit in enumerate(num_str):
        if digit != '0':
            power = length - i - 1
            if power == 0:
                result_parts.append(digit)
            else:
                result_parts.append(f"{digit}*1{'0' * power}")

    return "+".join(result_parts)

# Read input and process
num = int(input())
print(expanded_form(num), end='')

