def alphabet_war(fight):
    # Define the power of each letter
    left_power = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_power = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Convert string to list for easier manipulation
    fight_list = list(fight)
    length = len(fight_list)

    # Mark positions to be destroyed by bombs
    destroyed = [False] * length

    # Apply bomb effects
    for i in range(length):
        if fight_list[i] == '*':
            # Destroy the bomb itself
            destroyed[i] = True
            # Destroy adjacent positions if they exist
            if i > 0:
                destroyed[i-1] = True
            if i < length - 1:
                destroyed[i+1] = True

    # Calculate powers of surviving letters
    left_sum = 0
    right_sum = 0

    for i in range(length):
        if not destroyed[i]:  # If the character survived
            char = fight_list[i]
            if char in left_power:
                left_sum += left_power[char]
            elif char in right_power:
                right_sum += right_power[char]

    # Determine the winner
    if left_sum > right_sum:
        return "Left side wins!"
    elif right_sum > left_sum:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and call the function
fight = input().strip()
result = alphabet_war(fight)
print(result)

