import sys

def getAllPrimeFactors(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return []

    if n == 1:
        return [1]

    factors = []
    divisor = 2

    while divisor * divisor <= n:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1

    if n > 1:
        factors.append(n)

    return factors

def getUniquePrimeFactorsWithCount(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return [[], []]

    if n == 1:
        return [[1], [1]]

    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]

    unique_primes = []
    counts = []

    for factor in factors:
        if factor not in unique_primes:
            unique_primes.append(factor)
            counts.append(1)
        else:
            index = unique_primes.index(factor)
            counts[index] += 1

    return [unique_primes, counts]

def getUniquePrimeFactorsWithProducts(n):
    # Input validation
    if not isinstance(n, int) or n <= 0:
        return []

    if n == 1:
        return [1]

    unique_factors_with_count = getUniquePrimeFactorsWithCount(n)
    if not unique_factors_with_count[0]:
        return []

    primes = unique_factors_with_count[0]
    counts = unique_factors_with_count[1]

    products = []
    for i in range(len(primes)):
        products.append(primes[i] ** counts[i])

    return products

# Read input
try:
    n = int(input())
except ValueError:
    # If input is not a number
    print([])
    sys.exit()

# Call function and print result
result = getAllPrimeFactors(n)
print(result)

