def generate_polydivisible_numbers():
    # Precompute all polydivisible numbers using BFS
    from collections import deque

    polydivisible_numbers = []
    queue = deque([str(i) for i in range(1, 10)])  # Start with digits 1-9

    while queue:
        current = queue.popleft()
        polydivisible_numbers.append(int(current))

        # Try extending with digits 0-9
        for digit in range(0, 10):
            next_num_str = current + str(digit)
            # Check if the new number is polydivisible
            if int(next_num_str) % len(next_num_str) == 0:
                queue.append(next_num_str)

    return sorted(polydivisible_numbers)

def find_next_polydivisible(n, polydivisible_list):
    # Find the first polydivisible number greater than n
    for num in polydivisible_list:
        if num > n:
            return num
    return None

