def is_strictly_increasing_after_removal(sequence):
    # Count violations and record the first violation index
    violations = 0
    first_violation_index = -1

    for i in range(len(sequence) - 1):
        if sequence[i] >= sequence[i + 1]:
            violations += 1
            if first_violation_index == -1:
                first_violation_index = i

    # If no violations, already strictly increasing
    if violations == 0:
        return True

    # If more than one violation, impossible to fix with one removal
    if violations > 1:
        return False

    # Exactly one violation at first_violation_index
    # Try removing sequence[first_violation_index]
    def is_strictly_increasing(seq):
        for i in range(len(seq) - 1):
            if seq[i] >= seq[i + 1]:
                return False
        return True

    # Create sequences with either element removed
    seq_remove_i = sequence[:first_violation_index] + sequence[first_violation_index + 1:]
    seq_remove_i_plus_1 = sequence[:first_violation_index + 1] + sequence[first_violation_index + 2:]

    # Check if either removal results in strictly increasing sequence
    return is_strictly_increasing(seq_remove_i) or is_strictly_increasing(seq_remove_i_plus_1)

# Read input
sequence = list(map(int, input().strip()[1:-1].split(',')))

# Call function and print result
result = is_strictly_increasing_after_removal(sequence)
print(result)

