def is_prime(num):
    if num < 2:
        return False
    if num == 2:
        return True
    if num % 2 == 0:
        return False
    for i in range(3, int(num**0.5) + 1, 2):
        if num % i == 0:
            return False
    return True

def is_mersenne_prime(n):
    if not is_prime(n):
        return False

    mersenne_candidate = (2 ** n) - 1

    return is_prime(mersenne_candidate)

n = int(input())
print(is_mersenne_prime(n))

