def mix(s1, s2):
    # Count lowercase letters in both strings
    count1 = {}
    count2 = {}

    for char in s1:
        if 'a' <= char <= 'z':
            count1[char] = count1.get(char, 0) + 1

    for char in s2:
        if 'a' <= char <= 'z':
            count2[char] = count2.get(char, 0) + 1

    # Determine max counts and which string has the max
    result_parts = []

    # Get all unique lowercase letters from both strings
    all_letters = set(count1.keys()) | set(count2.keys())

    for letter in all_letters:
        c1 = count1.get(letter, 0)
        c2 = count2.get(letter, 0)

        max_count = max(c1, c2)

        # Only consider if max count > 1
        if max_count > 1:
            if c1 > c2:
                prefix = "1:"
            elif c2 > c1:
                prefix = "2:"
            else:  # c1 == c2
                prefix = "=:"  # Changed back to "=:" for Python

            result_parts.append(prefix + letter * max_count)

    # Sort by length (descending) and then lexicographically (ascending)
    result_parts.sort(key=lambda x: (-len(x), x))

    return "/".join(result_parts)

# Read input
s1 = input()
s2 = input()

