def parse_int(string):
    # Define word to number mappings
    units = {
        "zero": 0, "one": 1, "two": 2, "three": 3, "four": 4, "five": 5,
        "six": 6, "seven": 7, "eight": 8, "nine": 9, "ten": 10,
        "eleven": 11, "twelve": 12, "thirteen": 13, "fourteen": 14, "fifteen": 15,
        "sixteen": 16, "seventeen": 17, "eighteen": 18, "nineteen": 19
    }

    tens = {
        "twenty": 20, "thirty": 30, "forty": 40, "fifty": 50,
        "sixty": 60, "seventy": 70, "eighty": 80, "ninety": 90
    }

    scales = {
        "hundred": 100,
        "thousand": 1000,
        "million": 1000000
    }

    # Handle empty string
    if not string:
        return 0

    # Split the string into words and handle "and"
    words = string.replace(' and ', ' ').split()

    if not words:
        return 0

    total = 0
    current = 0

    i = 0
    while i < len(words):
        word = words[i]

        # Handle hyphenated compound numbers
        if '-' in word:
            parts = word.split('-')
            if len(parts) == 2 and parts[0] in tens and parts[1] in units:
                current += tens[parts[0]] + units[parts[1]]
            i += 1
        elif word in units:
            current += units[word]
            i += 1
        elif word in tens:
            current += tens[word]
            i += 1
        elif word in scales:
            if word == "hundred":
                current *= scales[word]
            else:  # thousand or million
                total += current * scales[word]
                current = 0
            i += 1
        else:
            i += 1

    total += current
    return total

# Read input and solve
input_string = input().strip()
result = parse_int(input_string)
print(result)

