def proper_fractions(d):
    if d <= 1:
        return 0

    # Euler's totient function
    result = d

    # Check for factor 2
    if d % 2 == 0:
        result -= result // 2
        while d % 2 == 0:
            d //= 2

    # Check for odd factors from 3 onwards
    i = 3
    while i * i <= d:
        if d % i == 0:
            result -= result // i
            while d % i == 0:
                d //= i
        i += 2

    # If d is still > 1, then it's a prime factor
    if d > 1:
        result -= result // d

    return result

# Read input and solve
d = int(input())
print(proper_fractions(d))

