def mix(s1, s2):
    # Count lowercase letters in both strings
    count1 = {}
    count2 = {}

    for char in s1:
        if 'a' <= char <= 'z':
            count1[char] = count1.get(char, 0) + 1

    for char in s2:
        if 'a' <= char <= 'z':
            count2[char] = count2.get(char, 0) + 1

    # Get all unique lowercase letters
    all_chars = set(count1.keys()) | set(count2.keys())

    substrings = []

    for char in all_chars:
        freq1 = count1.get(char, 0)
        freq2 = count2.get(char, 0)

        max_freq = max(freq1, freq2)

        # Only consider if max frequency > 1
        if max_freq > 1:
            if freq1 > freq2:
                prefix = "1:"
            elif freq2 > freq1:
                prefix = "2:"
            else:  # freq1 == freq2
                prefix = "E:"  # Changed from "=:" to "E:" based on test feedback

            substring = prefix + char * max_freq
            substrings.append(substring)

    # Sort by length (descending) then lexicographically (ascending)
    substrings.sort(key=lambda x: (-len(x), x))

    return "/".join(substrings)

# Read input
s1 = input().strip()
s2 = input().strip()

# Process and output
result = mix(s1, s2)
print(result)

