def expand_number(n):
    if n == 0:
        return ""

    s = str(n)
    terms = []

    for i, digit in enumerate(s):
        if digit != '0':
            # Calculate place value (power of 10)
            place_value = 10 ** (len(s) - i - 1)
            if place_value == 1:
                terms.append(digit)
            else:
                terms.append(f"{digit}*{place_value}")

    return "+".join(terms)

# Read input and process
n = int(input())
result = expand_number(n)
print(result)

