def are_anagrams(word1, word2):
    # Two words are anagrams if they have the same sorted characters
    return sorted(word1) == sorted(word2)

def anagrams(word, words_list):
    result = []
    for w in words_list:
        if are_anagrams(word, w):
            result.append(w)
    return result

# Read input
word = input().strip()
words_list = eval(input().strip())  # This will parse the Python list format

# Find anagrams
result = anagrams(word, words_list)

# Print result
print(result)

def alphabet_war(fight):
    n = len(fight)
    destroyed = [False] * n

    # Process bombs - mark destroyed positions
    for i in range(n):
        if fight[i] == '*':
            # Destroy the bomb position
            destroyed[i] = True
            # Destroy left adjacent position
            if i > 0:
                destroyed[i-1] = True
            # Destroy right adjacent position
            if i < n-1:
                destroyed[i+1] = True

    # Calculate powers
    left_power = 0
    right_power = 0

    # Left side letter powers
    left_letters = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    # Right side letter powers
    right_letters = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    # Calculate power for surviving letters
    for i in range(n):
        if not destroyed[i]:  # If position survived
            char = fight[i]
            if char in left_letters:
                left_power += left_letters[char]
            elif char in right_letters:
                right_power += right_letters[char]

    # Determine winner
    if left_power > right_power:
        return "Left side wins!"
    elif right_power > left_power:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Take input and call function
