import sys

def getAllPrimeFactors(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 1:
        return [1]

    factors = []
    divisor = 2
    while n > 1:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1
        if divisor * divisor > n and n > 1:
            factors.append(n)
            break
    return factors

def getUniquePrimeFactorsWithCount(n):
    if not isinstance(n, int) or n <= 0:
        return [[], []]
    if n == 1:
        return [[1], [1]]

    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]

    unique_factors = []
    counts = []
    i = 0
    while i < len(factors):
        current = factors[i]
        count = 0
        j = i
        while j < len(factors) and factors[j] == current:
            count += 1
            j += 1
        unique_factors.append(current)
        counts.append(count)
        i = j
    return [unique_factors, counts]

def getUniquePrimeFactorsWithProducts(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 1:
        return [1]

    unique_factors, counts = getUniquePrimeFactorsWithCount(n)
    if not unique_factors:
        return []

    products = []
    for i in range(len(unique_factors)):
        products.append(unique_factors[i] ** counts[i])
    return products

# Read input and execute
n = int(input())
result = getAllPrimeFactors(n)
print(result)
