def solve(s):
    vowels = set('aeiou')
    result = []

    for char in s:
        original_char = char
        new_char = char

        # Handle special exceptions first
        if char == 'c':
            new_char = 'b'
        elif char == 'o':
            new_char = 'n'
        elif char == 'd':
            new_char = 'a'
        elif char == 'e':
            new_char = 'a'
        # Regular processing
        elif char in vowels:
            # Move vowels back 5 places
            new_pos = ord(char) - 5
            if new_pos < ord('a'):
                new_pos = ord('z') - (ord('a') - new_pos - 1)
            new_char = chr(new_pos)
        else:
            # Consonants (excluding the special cases already handled)
            # Move consonants forward 9 places
            new_pos = ord(char) + 9
            if new_pos > ord('z'):
                new_pos = ord('a') + (new_pos - ord('z') - 1)
            new_char = chr(new_pos)

        # Check if new character is one of the special ones
        if new_char in 'code':
            new_char = original_char

        result.append(new_char)

    return ''.join(result)

# Read input and process
