def transform_word(word):
    # Separate the actual word from punctuation
    actual_word = ""
    trailing_punct = ""

    for i, char in enumerate(word):
        if char.isalnum() or char in "'-":
            actual_word += char
        else:
            trailing_punct = word[i:]
            break

    if not actual_word:
        return word

    # Apply transformations in order of precedence
    if len(actual_word) == 1:
        # Rule 3: Convert to "0"
        return "0" + trailing_punct
    elif len(actual_word) > 6 or actual_word.lower().count('t') >= 2:
        # Rule 1: Convert backwards
        return actual_word[::-1] + trailing_punct
    elif len(actual_word) == 2:
        # Rule 2: Convert to uppercase (exactly 2 characters)
        return actual_word.upper() + trailing_punct
    else:
        # No transformation for this rule, check "before a comma" case separately
        return actual_word + trailing_punct

def process_sentence(sentence):
    words = sentence.split(' ')
    result_words = []

    for i, word in enumerate(words):
        # Check if current word is before a comma
        is_before_comma = False
        if i < len(words) - 1 and words[i + 1].startswith(','):
            is_before_comma = True

        # Separate actual word from punctuation
        actual_word = ""
        trailing_punct = ""
        for j, char in enumerate(word):
            if char.isalnum() or char in "'-":
                actual_word += char
            else:
                trailing_punct = word[j:]
                break

        if not actual_word:
            result_words.append(word)
            continue

        # Apply transformations
        if len(actual_word) == 1:
            # Rule 3: Convert to "0"
            result_words.append("0" + trailing_punct)
        elif len(actual_word) > 6 or actual_word.lower().count('t') >= 2:
            # Rule 1: Convert backwards
            result_words.append(actual_word[::-1] + trailing_punct)
        elif len(actual_word) == 2 or is_before_comma:
            # Rule 2: Convert to uppercase
            result_words.append(actual_word.upper() + trailing_punct)
        else:
            # No transformation
            result_words.append(word)

    return ' '.join(result_words)

