def calc_tip(p, r):
    # Step 1: Round price P to the tens place
    if p % 10 >= 5:
        rounded_p = ((p // 10) + 1) * 10
    else:
        rounded_p = (p // 10) * 10

    # Step 2: Drop the singles digit to get base tip T
    T = rounded_p // 10

    # Step 3: Apply satisfaction rating R
    if r == 1:
        T = T + 1
    elif r == 0:
        T = T - 1
    elif r == -1:
        T = (T // 2) - 1

    # Ensure T is non-negative
    return max(0, T)

# Read input and call function
p, r = map(int, input().split())
result = calc_tip(p, r)
print(result)

